args.content:
/* 基本設定 */
body {
    font-family: 'Yu Gothic', '游ゴシック体', 'ヒラギノ角ゴ Pro W3', 'Hiragino Kaku Gothic Pro', 'メイリオ', Meiryo, sans-serif;
    line-height: 1.8;
    color: #333;
    background-color: #fff;
    margin: 0;
    padding: 0;
}

a {
    color: #3498db;
    text-decoration: none;
    transition: color 0.3s;
}

a:hover {
    color: #2980b9;
}

img {
    max-width: 100%;
    height: auto;
}

.container {
    width: 90%;
    max-width: 960px;
    margin-left: auto;
    margin-right: auto;
}

/* ヘッダー */
.main-header {
    background-color: #fff;
    padding: 20px 0;
    border-bottom: 1px solid #eee;
    position: sticky;
    top: 0;
    z-index: 100;
}

.main-header .container {
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.product-name {
    margin: 0;
    font-size: 24px;
    color: #3498db;
}

.main-nav ul {
    margin: 0;
    padding: 0;
    list-style: none;
    display: flex;
}

.main-nav li {
    margin-left: 30px;
}

.main-nav a {
    font-weight: bold;
}

/* メインセクション共通 */
main section {
    padding: 60px 0;
}

.section-title {
    text-align: center;
    font-size: 32px;
    margin-top: 0;
    margin-bottom: 50px;
}

/* ヒーローセクション */
.hero-section {
    text-align: center;
    padding-top: 80px;
    padding-bottom: 80px;
}

.catch-copy {
    font-size: 36px;
    margin-bottom: 40px;
}

.product-image {
    border-radius: 8px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}

/* 特徴セクション */
.features-section {
    background-color: #f9f9f9;
}

.feature-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: 40px;
    text-align: center;
}

.feature-item {
    background-color: #fff;
    padding: 30px;
    border-radius: 8px;
    box-shadow: 0 4px 15px rgba(0,0,0,0.05);
}

.feature-title {
    font-size: 20px;
    color: #3498db;
}

/* 価格セクション */
.pricing-table {
    width: 100%;
    border-collapse: collapse;
    text-align: center;
    box-shadow: 0 4px 15px rgba(0,0,0,0.05);
}

.pricing-table th, .pricing-table td {
    padding: 20px;
    border: 1px solid #eee;
}

.pricing-table thead {
    background-color: #3498db;
    color: #fff;
}

.pricing-table th {
    font-weight: bold;
}

.pricing-table tbody tr:nth-child(even) {
    background-color: #f9f9f9;
}

.button {
    display: inline-block;
    background-color: #3498db;
    color: #fff;
    padding: 10px 20px;
    border-radius: 5px;
    font-weight: bold;
    transition: background-color 0.3s;
}

.button:hover {
    background-color: #2980b9;
    color: #fff;
}

/* フッター */
.main-footer {
    background-color: #333;
    color: #fff;
    text-align: center;
    padding: 40px 0;
}

.company-info, .copyright {
    margin: 0;
    color: #aaa;
}

/* レスポンシブ対応 (スマートフォン向け) */
@media (max-width: 768px) {
    .main-header .container {
        flex-direction: column;
    }

    .product-name {
        margin-bottom: 20px;
    }

    .main-nav ul {
        width: 100%;
        justify-content: space-around;
    }

    .main-nav li {
        margin: 0;
    }

    .catch-copy {
        font-size: 28px;
    }

    .feature-grid {
        grid-template-columns: 1fr;
    }

    /* テーブルをカード形式に変更 */
    .pricing-table thead {
        display: none;
    }

    .pricing-table, .pricing-table tbody, .pricing-table tr, .pricing-table td {
        display: block;
        width: 100%;
    }

    .pricing-table tr {
        margin-bottom: 20px;
        border: 1px solid #eee;
        border-radius: 8px;
        overflow: hidden;
    }

    .pricing-table td {
        text-align: right;
        padding-left: 50%;
        position: relative;
        border: none;
        border-bottom: 1px solid #eee;
    }

    .pricing-table td:before {
        content: attr(data-label);
        position: absolute;
        left: 20px;
        width: calc(50% - 40px);
        text-align: left;
        font-weight: bold;
        color: #333;
    }

    .pricing-table td:last-child {
        border-bottom: none;
    }

    /* tdにdata-label属性を動的に付与するためのスクリプトが必要になりますが、
       CSSだけで表現するため、擬似要素でラベルを付与します。
       JSなしでやるには、HTMLにdata-label属性を追記する必要があります。
       ここでは、擬似要素で静的に設定します。*/
    .pricing-table tbody td:nth-of-type(1):before { content: 'プラン名'; }
    .pricing-table tbody td:nth-of-type(2):before { content: '月額料金'; }
    .pricing-table tbody td:nth-of-type(3):before { content: '主な機能'; }
    .pricing-table tbody td:nth-of-type(4):before { content: '申し込み'; }
}
